import React, {useState} from 'react' 
import { Link } from 'react-router-dom';
import Validation from './SignUpValidation'

function Signup(){

        const [values, setValues] = useState({
            name: '',
            email: '',
            password: ''
        })
        const handleInput = (event => {
            setValues(prev => ({...prev, [event.target.name]: [event.target.value]}))
        })
        const [errors, setErrors] = useState({})
        const handleSubmit =(event)=>{
            event.preventDefault();
            setErrors(Validation(values));
        }

    return(
        <div className='d-flex justify-content-center align-items-center bg-primary vh-100'>
        <div className='bg-white p-3 rounded w-25'>
            <h2>Sign-Up</h2>
            <form action="" onSubmit={handleInput}>
                <div className='mb-3'>
                    <label htmlFor="name">Name</label>
                    <input type="text" placeholder='Enter Name' name='name' className='form-control rounded-0'/>
                </div>
                <div className='mb-3'>
                    <label htmlFor="email">Email</label>
                    <input type="email" placeholder='Enter Email' name='email' className='form-control rounded-0'/>
                </div>
                <div className='mb-3'>
                    <label htmlFor="password">Password</label>
                    <input type="password" placeholder='Enter Password' name='password' className='form-control rounded-0'/>
                </div>
                <button className='btn btn-success w-100'><strong>Sign Up</strong></button>
                <p>You agree to our terms and policies</p>
                <Link to='/' className='btn btn-default border w-100'>Log In</Link>
            </form>
        </div>
    </div>
    )
}
export default Signup
